import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminUploads = () => {
  const [uploads, setUploads] = useState([]);

  useEffect(() => {
    fetchUploads();
  }, []);

  const fetchUploads = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/admin-uploads', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUploads(response.data);
    } catch (err) {
      console.error('Error fetching uploads:', err);
    }
  };

  return (
    <div>
      <h2>Admin Dashboard: User Uploads</h2>
      <ul>
        {uploads.map((upload) => (
          <li key={upload._id}>
            <h3>{upload.title}</h3>
            <p>{upload.description}</p>
            <p>Link: <a href={upload.link} target="_blank" rel="noopener noreferrer">{upload.link}</a></p>
            <p>Uploaded by: {upload.userId.name} ({upload.userId.email})</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminUploads;
