// src/components/ProblemDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProblemDetail = () => {
  const { id } = useParams();
  const [problem, setProblem] = useState(null);
  const [solution, setSolution] = useState('');

  useEffect(() => {
    const fetchProblem = async () => {
      const res = await axios.get(`http://localhost:5000/api/problems/api/problems/${id}`);
      setProblem(res.data);
    };

    fetchProblem();
  }, [id]);

  const handleSubmit = async () => {
    await axios.post('/api/solutions', { userId: 'dummyUserId', problemId: id, solution });
  };

  return (
    <div>
      {problem && (
        <div>
          <h1>{problem.title}</h1>
          <p>{problem.content}</p>
          <textarea value={solution} onChange={e => setSolution(e.target.value)} />
          <button onClick={handleSubmit}>Submit Solution</button>
        </div>
      )}
    </div>
  );
};

export default ProblemDetail;
