import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Quiz = () => {
    const { topic } = useParams();
    const [quiz, setQuiz] = useState(null);
    const [responses, setResponses] = useState([]);
    const [submitted, setSubmitted] = useState(false);

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/quiz/${topic}`);
                setQuiz(response.data);
                setResponses(response.data.questions.map(() => ''));
            } catch (error) {
                console.error('Error fetching quiz data:', error);
            }
        };
        fetchQuiz();
    }, [topic]);

    const handleChange = (index, value) => {
        const newResponses = [...responses];
        newResponses[index] = value;
        setResponses(newResponses);
    };

    const handleSubmit = async () => {
        try {
            const userInfo = { name: 'John Doe', email: 'john.doe@example.com' }; // Use dynamic values if available
            const results = quiz.questions.map((q, i) => ({
                question: q.question,
                correctAnswer: q.answer,
                userAnswer: responses[i] || '',
                isCorrect: q.answer === responses[i]
            }));
            await axios.post('http://localhost:5000/api/quiz/submit', { userInfo, topic, responses: results });
            setSubmitted(true);
        } catch (error) {
            console.error('Error submitting quiz:', error);
        }
    };

    if (!quiz) return <p>Loading...</p>;

    return (
        <div>
            <h1>Quiz on {quiz.topic}</h1>
            {quiz.questions.map((q, index) => (
                <div key={index}>
                    <p>{q.question}</p>
                    {q.options.map((option, oIndex) => (
                        <label key={oIndex}>
                            <input
                                type="radio"
                                name={`question-${index}`}
                                value={option}
                                onChange={(e) => handleChange(index, e.target.value)}
                            />
                            {option}
                        </label>
                    ))}
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
            {submitted && <p>Quiz submitted successfully!</p>}
        </div>
    );
};

export default Quiz;
