// src/pages/CompanyResearchPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompanyResearchPage = () => {
  const [resources, setResources] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/resources/company-research')
      .then(response => setResources(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1 className="text-4xl font-bold mb-4">Company Research</h1>
      {resources.map(resource => (
        <div key={resource._id} className="mb-4">
          <h2 className="text-2xl font-bold">{resource.resourceTitle}</h2>
          <p>{resource.resourceContent}</p>
          <a href={resource.resourceUrl} className="text-blue-500 hover:underline">Research</a>
        </div>
      ))}
    </div>
  );
};

export default CompanyResearchPage;
