import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const RoadmapSection = () => {
  const [roadmaps, setRoadmaps] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/roadmaps')
      .then(response => setRoadmaps(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1 className="text-4xl font-bold mb-4">RoadMap</h1>
      <img src="./roadmap.jpg" alt="roadmap" className="w-full h-40 object-cover rounded-lg mb-3" />
      {roadmaps.map((roadmap) => (
        <div key={roadmap._id} className="mb-4">
          <h2 className="text-xl font-bold">{roadmap.title}</h2>
          <p>{roadmap.description}</p>
          <Link to={`/roadmaps/${roadmap._id}`} className="text-primary hover:underline">Explore</Link>
        </div>
      ))}
    </div>
  );
};

export default RoadmapSection;
