// Profile.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Fetch user information (username in this case) from wherever it's stored (e.g., localStorage, API)
    const loggedInUser = JSON.parse(localStorage.getItem('user')); // Assuming user info is stored in localStorage
    if (loggedInUser && loggedInUser.username) {
      setUsername(loggedInUser.username);
    } else {
      // Redirect to sign-in if user info is not available
      navigate('/signin');
    }
  }, [navigate]);

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-bold mb-6">Profile</h1>
      <div className="bg-white shadow-md rounded-lg px-6 py-4">
        <p className="text-lg font-semibold mb-2">Username: {username}</p>
        <div className="mt-4">
          {/* Add other profile information or settings */}
          {/* Example: Change password, update profile picture, etc. */}
          <button className="bg-purple-600 text-white px-4 py-2 rounded-full shadow-md hover:bg-purple-700 transition duration-300">Change Password</button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
