import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'tailwindcss/tailwind.css';

const InterviewPreparationDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [reminders, setReminders] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newReminder, setNewReminder] = useState('');

  useEffect(() => {
    fetchTasks();
    fetchReminders();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tasks');
      setTasks(response.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const fetchReminders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/reminders');
      setReminders(response.data);
    } catch (err) {
      console.error('Error fetching reminders:', err);
    }
  };

  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:5000/tasks', { title: newTask });
      setTasks([...tasks, response.data]);
      setNewTask('');
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const addReminder = async () => {
    try {
      const response = await axios.post('http://localhost:5000/reminders', { title: newReminder, date: new Date() });
      setReminders([...reminders, response.data]);
      setNewReminder('');
    } catch (err) {
      console.error('Error adding reminder:', err);
    }
  };

  const deleteReminder = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/reminders/${id}`);
      setReminders(reminders.filter(reminder => reminder._id !== id));
    } catch (err) {
      console.error('Error deleting reminder:', err);
    }
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col">
      <header className="bg-gradient-to-r from-purple-500 to-indigo-500 py-4 px-6 flex justify-between items-center shadow-lg">
        <h1 className="text-3xl font-bold tracking-wider">Interview Preparation Dashboard</h1>
        <button className="bg-primary-foreground text-primary hover:bg-primary/80 hover:text-primary-foreground px-4 py-2 rounded-full shadow-md transition duration-300">Logout</button>
      </header>

      <main className="flex-1 p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Section title="Study Planner" description="Plan your study schedule here">
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-input p-3 rounded-lg shadow-inner">
              <input
                type="text"
                placeholder="Add task"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                className="w-full bg-transparent focus:outline-none"
              />
            </div>
            <button
              onClick={addTask}
              className="bg-gradient-to-r from-green-400 to-blue-500 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
            >
              Add
            </button>
          </div>
          <ul className="mt-4 space-y-2">
            {tasks.map((task) => (
              <ListItem key={task._id} task={task} onDelete={() => deleteTask(task._id)} />
            ))}
          </ul>
        </Section>

        <Section title="Coding Practice" description="Sharpen your coding skills">
          <img src="https://openui.fly.dev/openui/300x150.svg?text=Coding" alt="coding-practice" className="w-full h-32 object-cover rounded-lg mb-3" />
          <a href="#" className="text-primary hover:underline">Start Coding</a>
        </Section>

        <Section title="Behavioral Questions" description="Prepare for behavioral interviews">
          <img src="https://openui.fly.dev/openui/300x150.svg?text=Behavioral" alt="behavioral-questions" className="w-full h-32 object-cover rounded-lg mb-3" />
          <a href="#" className="text-primary hover:underline">Practice Questions</a>
        </Section>

        <Section title="Company Research" description="Explore information about target companies">
          <img src="https://openui.fly.dev/openui/300x150.svg?text=Company" alt="company-research" className="w-full h-32 object-cover rounded-lg mb-3" />
          <a href="#" className="text-primary hover:underline">View Details</a>
        </Section>

        <Section title="Progress Tracking" description="Track your preparation progress">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm">Coding Practice: 50%</p>
              <div className="bg-primary h-1 mt-1 rounded-full">
                <div className="bg-primary-foreground h-full rounded-full" style={{ width: '50%' }}></div>
              </div>
            </div>
            <button className="text-primary hover:underline">View Details</button>
          </div>
        </Section>

        <Section title="Reminders" description="Set reminders for important tasks">
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-input p-3 rounded-lg shadow-inner">
              <input
                type="text"
                placeholder="Add reminder"
                value={newReminder}
                onChange={(e) => setNewReminder(e.target.value)}
                className="w-full bg-transparent focus:outline-none"
              />
            </div>
            <button
              onClick={addReminder}
              className="bg-gradient-to-r from-green-400 to-blue-500 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
            >
              Add
            </button>
          </div>
          <ul className="mt-4 space-y-2">
            {reminders.map((reminder) => (
              <ListItem key={reminder._id} task={reminder} onDelete={() => deleteReminder(reminder._id)} />
            ))}
          </ul>
        </Section>

        <Section title="Resources" description="Access helpful articles, videos, and books">
          <ul className="space-y-2">
            <li><a href="#" className="text-primary hover:underline">Article 1</a></li>
            <li><a href="#" className="text-primary hover:underline">Video Tutorial</a></li>
            <li><a href="#" className="text-primary hover:underline">Recommended Books</a></li>
          </ul>
        </Section>
      </main>

      <footer className="bg-gradient-to-r from-purple-500 to-indigo-500 py-4 px-6 text-primary-foreground text-sm text-center shadow-inner">
        &copy; 2023 Interview Prep Dashboard. All rights reserved.
      </footer>
    </div>
  );
};

const Section = ({ title, description, children }) => (
  <section className="bg-card p-6 rounded-lg shadow-md hover:shadow-xl transition duration-300 transform hover:-translate-y-1">
    <h2 className="text-xl font-semibold mb-3">{title}</h2>
    <p className="text-sm text-muted-foreground mb-4">{description}</p>
    {children}
  </section>
);

const ListItem = ({ task, onDelete }) => (
  <li className="flex items-center justify-between py-2 border-b border-border">
    <span>{task.title}</span>
    <button onClick={onDelete} className="text-destructive hover:text-red-600 transition duration-300">Delete</button>
  </li>
);

export default InterviewPreparationDashboard;
