import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Group() {
  const { id } = useParams();
  const [group, setGroup] = useState({});
  const [discussions, setDiscussions] = useState([]);
  const [newDiscussion, setNewDiscussion] = useState({ title: '', content: '' });

  useEffect(() => {
    axios.get(`http://localhost:5000/api/groups/${id}`)
      .then(response => setGroup(response.data))
      .catch(error => console.error(error));

    axios.get(`http://localhost:5000/api/discussions/group/${id}`)
      .then(response => setDiscussions(response.data))
      .catch(error => console.error(error));
  }, [id]);

  const handleChange = (e) => {
    setNewDiscussion({ ...newDiscussion, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/api/discussions', { ...newDiscussion, groupId: id, author: 'user-id' })
      .then(response => setDiscussions([...discussions, response.data]))
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h2>{group.name}</h2>
      <h3>Discussions</h3>
      <ul>
        {discussions.map(discussion => (
          <li key={discussion._id}>
            <h4>{discussion.title}</h4>
            <p>{discussion.content}</p>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={newDiscussion.title}
          onChange={handleChange}
          placeholder="Discussion Title"
          required
        />
        <textarea
          name="content"
          value={newDiscussion.content}
          onChange={handleChange}
          placeholder="Discussion Content"
          required
        />
        <button type="submit">Create Discussion</button>
      </form>
    </div>
  );
}

export default Group;
