import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Quiz = ({ topic }) => {
    const [quizData, setQuizData] = useState([]);
    const [userResponses, setUserResponses] = useState({});
    const [answers, setAnswers] = useState(null);
    const [userInfo, setUserInfo] = useState({ name: '', email: '' });

    useEffect(() => {
        axios.get(`http://localhost:5000/api/quiz/${topic}`)
            .then(response => setQuizData(response.data.questions))
            .catch(error => console.error('Error fetching quiz data:', error));
    }, [topic]);

    const handleChange = (e, questionId) => {
        setUserResponses({
            ...userResponses,
            [questionId]: e.target.value,
        });
    };

    const handleUserInfoChange = (e) => {
        const { name, value } = e.target;
        setUserInfo(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = () => {
        const responses = quizData.map((q) => ({
            question: q.question,
            correctAnswer: q.answer,
            userAnswer: userResponses[q._id] || '',
            isCorrect: q.answer === userResponses[q._id] || false,
        }));

        const submissionData = {
            userInfo,
            topic,
            responses
        };

        axios.post('http://localhost:5000/api/quiz/submit', submissionData)
            .then(response => setAnswers(response.data.responses))
            .catch(error => console.error('Error submitting quiz:', error));
    };

    return (
        <div>
            <h1>Quiz on {topic}</h1>
            <input
                type="text"
                name="name"
                placeholder="Name"
                value={userInfo.name}
                onChange={handleUserInfoChange}
            />
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={userInfo.email}
                onChange={handleUserInfoChange}
            />
            <div>
                {quizData.map((question) => (
                    <div key={question._id}>
                        <p>{question.question}</p>
                        {question.options.map((option, index) => (
                            <div key={index}>
                                <input
                                    type="radio"
                                    id={`question-${question._id}-option-${index}`}
                                    name={`question-${question._id}`}
                                    value={option}
                                    onChange={(e) => handleChange(e, question._id)}
                                    checked={userResponses[question._id] === option}
                                />
                                <label htmlFor={`question-${question._id}-option-${index}`}>{option}</label>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <button onClick={handleSubmit}>Submit</button>
            {answers && (
                <div>
                    <h2>Quiz Results</h2>
                    {answers.map((answer, index) => (
                        <div key={index}>
                            <p>Question: {answer.question}</p>
                            <p>Your Answer: {answer.userAnswer}</p>
                            <p>Correct Answer: {answer.correctAnswer}</p>
                            <p>{answer.isCorrect ? "Correct" : "Incorrect"}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Quiz;
