import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Quiz = () => {
    const [quizData, setQuizData] = useState([]);
    const [userResponses, setUserResponses] = useState({});
    const [answers, setAnswers] = useState(null);
    const [userInfo, setUserInfo] = useState({ name: '', email: '' });
    const [topic, setTopic] = useState('');

    useEffect(() => {
        if (topic) {
            axios.get(`http://localhost:5000/api/quiz?topic=${topic}`)
                .then(response => setQuizData(response.data))
                .catch(error => console.error('Error fetching quiz data:', error));
        }
    }, [topic]);

    const handleChange = (e, questionId) => {
        setUserResponses({
            ...userResponses,
            [questionId]: e.target.value,
        });
    };

    const handleUserInfoChange = (e) => {
        const { name, value } = e.target;
        setUserInfo(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = () => {
        const submissionData = {
            userInfo,
            responses: userResponses,
        };

        axios.post('http://localhost:5000/api/quiz/submit', submissionData)
            .then(response => setAnswers(response.data.answers))
            .catch(error => console.error('Error submitting quiz:', error));
    };

    return (
        <div>
            <h1>Quiz</h1>
            <input
                type="text"
                name="name"
                placeholder="Name"
                value={userInfo.name}
                onChange={handleUserInfoChange}
            />
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={userInfo.email}
                onChange={handleUserInfoChange}
            />
            <input
                type="text"
                placeholder="Topic"
                value={topic}
                onChange={e => setTopic(e.target.value)}
            />
            <div>
                {quizData.map((quiz, quizIndex) => (
                    <div key={quizIndex}>
                        <h2>{quiz.topic}</h2>
                        {quiz.questions.map((question, questionIndex) => (
                            <div key={question.id}>
                                <p>{question.question}</p>
                                {question.options.map((option, optionIndex) => (
                                    <div key={optionIndex}>
                                        <input
                                            type="radio"
                                            id={`question-${question.id}-option-${optionIndex}`}
                                            name={`question-${question.id}`}
                                            value={option}
                                            onChange={(e) => handleChange(e, question.id)}
                                        />
                                        <label htmlFor={`question-${question.id}-option-${optionIndex}`}>{option}</label>
                                    </div>
                                ))}
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <button onClick={handleSubmit}>Submit</button>
            {answers && (
                <div>
                    <h2>Quiz Results</h2>
                    {answers.map((answer, index) => (
                        <div key={index}>
                            <p>Question: {answer.question}</p>
                            <p>Your Answer: {answer.userAnswer}</p>
                            <p>Correct Answer: {answer.correctAnswer}</p>
                            <p>{answer.isCorrect ? "Correct" : "Incorrect"}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Quiz;
