import React from 'react';
import { FaCalendarAlt, FaBell, FaBook, FaCode, FaQuestion, FaFileAlt, FaRobot } from 'react-icons/fa';

const LandingPage = () => {
  return (
    <div className="font-sans text-center">
      <header className="bg-gray-100 p-8">
        <h1 className="text-4xl font-bold mb-4">Welcome to StudyHub</h1>
        <div className="space-x-4">
          <button className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600">Get Started</button>
          <button className="bg-gray-500 text-white px-6 py-2 rounded-md hover:bg-gray-600">Sign In</button>
        </div>
      </header>
      <main className="py-12">
        <section className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
          <div className="flex flex-col items-center">
            <FaCalendarAlt size={50} className="text-blue-500 mb-4" />
            <h3 className="text-xl font-semibold">Study Planner</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaBell size={50} className="text-yellow-500 mb-4" />
            <h3 className="text-xl font-semibold">Reminders</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaBook size={50} className="text-green-500 mb-4" />
            <h3 className="text-xl font-semibold">Resources</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaCode size={50} className="text-red-500 mb-4" />
            <h3 className="text-xl font-semibold">Coding Questions</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaQuestion size={50} className="text-purple-500 mb-4" />
            <h3 className="text-xl font-semibold">Behavioral Questions</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaFileAlt size={50} className="text-indigo-500 mb-4" />
            <h3 className="text-xl font-semibold">Resume Templates</h3>
          </div>
          <div className="flex flex-col items-center">
            <FaRobot size={50} className="text-pink-500 mb-4" />
            <h3 className="text-xl font-semibold">Chat Assistant</h3>
          </div>
        </section>
        <section className="bg-gray-100 p-8 rounded-md shadow-md">
          <h2 className="text-2xl font-bold mb-6">Contact Us</h2>
          <form className="space-y-4">
            <div className="flex flex-col">
              <label htmlFor="name" className="mb-2 font-medium">Name:</label>
              <input type="text" id="name" name="name" className="px-4 py-2 border rounded-md" />
            </div>
            <div className="flex flex-col">
              <label htmlFor="email" className="mb-2 font-medium">Email:</label>
              <input type="email" id="email" name="email" className="px-4 py-2 border rounded-md" />
            </div>
            <div className="flex flex-col">
              <label htmlFor="message" className="mb-2 font-medium">Message:</label>
              <textarea id="message" name="message" className="px-4 py-2 border rounded-md" rows="4"></textarea>
            </div>
            <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600">Submit</button>
          </form>
        </section>
      </main>
    </div>
  );
}

export default LandingPage;
