// src/pages/CompanyResearchPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompanyResearchPage = () => {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    axios.get('/company-research')
      .then(response => setCompanies(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1 className="text-4xl font-bold mb-4">Company Research</h1>
      {companies.map(company => (
        <div key={company._id} className="mb-4">
          <h2 className="text-2xl font-bold">{company.title}</h2>
          <p>{company.description}</p>
          <a href={company.link} className="text-blue-500 hover:underline">View Details</a>
        </div>
      ))}
    </div>
  );
};

export default CompanyResearchPage;
