import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import { useNavigate ,Navigate} from 'react-router-dom';
import ArticlePage from './ArticlePage';
import VideoTutorialPage from './VideoTutorialPage';
import RecommendedBooksPage from './RecommendedBooksPage';
import CodingPracticePage from './CodingPracticePage';
import BehavioralQuestionPage from './BehavioralQuestionPage';
import CompanyResearchPage from './CompanyResearchPage';
import AdminDashboard from './AdminDashboard';
import RoadmapSection from './RoadmapSection';
import RoadmapDetailPage from './RoadmapDetailPage';
import ChatInterface from './ChatInterface';
import ResourcePage from './ResourcePage';
import Avbadmin from './Avbadmin';
import ProgressTracker from './ProgressTracker';
import SignIn from './pages/SignIn';
import SignUp from './pages/SignUp';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserCircle, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

const InterviewPreparationDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [reminders, setReminders] = useState([]);
  const [resources, setResources] = useState([]);
  const [progress, setProgress] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newReminder, setNewReminder] = useState('');

  const [inputValue, setInputValue] = useState('');
  const [reminderList, setReminderList] = useState([]);
  const [alertEnabled, setAlertEnabled] = useState(true);
  const [lastReminderTime, setLastReminderTime] = useState(null);

  // Function to add a new reminder
  const addReminder = () => {
    if (inputValue.trim() !== '') {
      const newReminderObj = { _id: Date.now(), text: inputValue };
      setReminderList([...reminderList, newReminderObj]);
      setInputValue('');
      setLastReminderTime(new Date()); // Update last reminder time
    }
  };

  // Function to delete a reminder
  const deleteReminder = (reminderId) => {
    setReminderList(reminderList.filter((reminder) => reminder._id !== reminderId));
  };

  // UseEffect to handle the reminder alert every 10 minutes
  useEffect(() => {
    const reminderAlertInterval = setInterval(() => {
      if (alertEnabled && lastReminderTime) {
        const currentTime = new Date();
        const timeDifference = currentTime.getTime() - lastReminderTime.getTime();
        const minutesDifference = timeDifference / (1000 * 60);

        // Show alert if it's been at least 10 minutes since the last reminder
        if (minutesDifference >= 10) {
          alert('It\'s time to check your reminders!');
        }
      }
    }, 600000); // 10 minutes in milliseconds

    return () => clearInterval(reminderAlertInterval);
  }, [alertEnabled, lastReminderTime]);


  useEffect(() => {
    fetchTasks();
    fetchReminders();
    fetchResources();
    fetchProgress();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tasks');
      setTasks(response.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const fetchReminders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/reminders');
      setReminders(response.data);
    } catch (err) {
      console.error('Error fetching reminders:', err);
    }
  };

  const fetchResources = async () => {
    try {
      const response = await axios.get('http://localhost:5000/resources');
      setResources(response.data);
    } catch (err) {
      console.error('Error fetching resources:', err);
    }
  };

  const fetchProgress = async () => {
    try {
      const response = await axios.get('http://localhost:5000/progress');
      setProgress(response.data);
    } catch (err) {
      console.error('Error fetching progress:', err);
    }
  };

  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:5000/tasks', { title: newTask });
      setTasks([...tasks, response.data]);
      setNewTask('');
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const addReminders = async () => {
    try {
      const response = await axios.post('http://localhost:5000/reminders', { title: newReminder, date: new Date() });
      setReminders([...reminders, response.data]);
      setNewReminder('');
    } catch (err) {
      console.error('Error adding reminder:', err);
    }
  };

  const deleteReminders = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/reminders/${id}`);
      setReminders(reminders.filter(reminder => reminder._id !== id));
    } catch (err) {
      console.error('Error deleting reminder:', err);
    }
  };

  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear the JWT token from localStorage
    localStorage.removeItem('token');
    // Redirect to the sign-in page
    navigate('/signin');
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col">
     <header className="h-30 fixed top-0 left-0 right-0 bg-gradient-to-r from-purple-500/50 to-indigo-500/50 py-4 px-6 flex justify-between items-center shadow-lg ">
      <div className="flex items-center">
        <img className="h-10 w-auto mr-2" src="/finallogos.png" alt="Logo" /> 
        <h1 className="text-3xl font-bold tracking-wider text-white font-serif ">FailToFly</h1>
      </div>
      <div className="flex items-center">
        <button
          className="bg-white text-purple-600 hover:bg-gray-200 px-4 py-2 rounded-full shadow-md transition duration-300 mr-4 flex items-center"
          onClick={handleLogout}
          aria-label="Logout"
        >
          <FontAwesomeIcon icon={faSignOutAlt} className="mr-2" />
          Logout
        </button>
        <div className="relative">
          <button className="bg-white text-purple-600 hover:bg-gray-200 px-3 py-2 rounded-full shadow-md transition duration-300 flex items-center" aria-label="Profile">
            <FontAwesomeIcon icon={faUserCircle} className="mr-2" />
            Profile
          </button>
        </div>
      </div>
    </header>


      <main className="mt-16  flex-1 p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Section title="Study Planner" description="Plan your study schedule here">
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-input p-3 rounded-lg shadow-inner flex items-center">
              <input
                type="text"
                placeholder="Add task"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                className="w-full text-black bg-transparent focus:outline-none"
              />
            </div>
            <button
              onClick={addTask}
              className="bg-gradient-to-r from-purple-500/20 to-indigo-500/50 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
            >
              Add
            </button>
          </div>
          <ul className="mt-4 space-y-2">
            {tasks.map((task) => (
              <ListItem key={task._id} item={task} onDelete={() => deleteTask(task._id)} />
            ))}
          </ul>
        </Section>

        <Section title="Reminders" description="Set important reminders">
          <div className="grid grid-cols-2 gap-3">
            <div className="bg-input p-3 rounded-lg shadow-inner">
              <input
                type="text"
                placeholder="Add reminder"
                value={newReminder}
                onChange={(e) => setNewReminder(e.target.value)}
                className="w-full text-black bg-transparent focus:outline-none"
              />
            </div>
            <button
              onClick={addReminder}
              className="bg-gradient-to-r from-purple-500/20 to-indigo-500/50 text-white px-5 py-2 rounded-full shadow-md transition duration-300"
            >
              Add
            </button>
          </div>
          <ul className="mt-4 space-y-2">
            {reminders.map((reminder) => (
              <ListItem key={reminder._id} item={reminder} onDelete={() => deleteReminder(reminder._id)} />
            ))}
          </ul>
        </Section>

        <Section title="Resources" description="Access helpful articles, videos, and books">
          <ul className="space-y-2">
            <li><Link to="/article-1" className="text-primary text-black hover:underline">Article 1</Link></li>
            <li><Link to="/video-tutorial" className="text-primary text-black hover:underline">Video Tutorial</Link></li>
            <li><Link to="/recommended-books" className="text-primary text-black hover:underline">Recommended Books</Link></li>
          </ul>
        </Section>

        <Section title="Progress Tracker" description="Track your preparation progress">
        <img src="./progress.png" alt="coding-practice" className="w-full h-40 object-cover rounded-lg mb-3" />
        <Link to="/topics" className="text-primary hover:underline">Track</Link>
        </Section>
        <Section title="Coding Practice" description="Sharpen your coding skills">
          <img src="./codingimage.png" alt="coding-practice" className="w-full h-40 object-cover rounded-lg mb-3" />
          <Link to="/coding" className="text-primary hover:underline">Start Coding</Link>
        </Section>

        <Section title="Behavioral Questions" description="Prepare for behavioral interviews">
          <img src="./bquestions.jpg" alt="behavioral-questions" className="w-full h-40 object-cover rounded-lg mb-3" />
          <Link to="/behavioral-questions" className="text-primary hover:underline">Practice Questions</Link>
        </Section>

        <Section title="Company Research" description="Explore information about target companies">
          <img src="./company.jpg" alt="company-research" className="w-full h-40 object-cover rounded-lg mb-3" />
          <Link to="/company-research" className="text-primary hover:underline">View Details</Link>
        </Section>

        <Section title="RoadMap" description="Explore road maps">
          <img src="./roadmap.jpg" alt="roadmap" className="w-full h-40 object-cover rounded-lg mb-3" />
          <Link to="/roadmaps" className="text-primary hover:underline">Explore</Link>
        </Section>

        <Section title="Chatbot" description="Ask Anything">
          <ChatInterface />
        </Section>
      </main>
    </div>
  );
};

const Section = ({ title, description, children }) => (
  <div className="bg-white p-6 rounded-lg shadow-md">
    <h2 className="text-black text-3xl font-bold mb-2 fontsize-20">{title}</h2>
    <p className="text-green-600 mb-4">{description}</p>
    {children}
  </div>
);

const ListItem = ({ item, onDelete }) => (
  <li className="flex justify-between text-black items-center p-3 bg-secondary rounded-md shadow-sm">
    <span>{item.title}</span>
    <button onClick={onDelete} className="bg-red-500 text-white px-3 py-1 rounded-full">Delete</button>
  </li>
);

const ProgressItem = ({ progress }) => (
  <li className="flex justify-between items-center p-3 bg-secondary rounded-md shadow-sm">
    <span>{progress.title}</span>
    <div className="relative w-1/2 h-4 bg-gray-200 rounded-full">
      <div
        className="absolute top-0 left-0 h-4 bg-blue-500 rounded-full"
        style={{ width: `${progress.percentage}%` }}
      ></div>
    </div>
  </li>
);

const App = () => {

  return(
  <Router>
    <Routes>
      <Route path="/signin" element={<SignIn />} />
      <Route path="/signup" element={<SignUp />} />
      <Route path="/" element={<InterviewPreparationDashboard />} />
      <Route path="/adminres/:endpoint" element={<ResourcePage />} />
      <Route path="/admin" element={<AdminDashboard />} /> 
      <Route path="/topics" element = {<ProgressTracker/>} />
      <Route path="/avbadmin" element={<Avbadmin />} />
      <Route path="/roadmaps" element={<RoadmapSection />} />
      <Route path="/roadmaps/:id" element={<RoadmapDetailPage />} />
      <Route path="/coding" element={<CodingPracticePage />} />
      <Route path="/behavioral-questions" element={<BehavioralQuestionPage />} />
      <Route path="/company-research" element={<CompanyResearchPage />} />
      <Route path="/article-1" element={<ArticlePage />} />
      <Route path="/video-tutorial" element={<VideoTutorialPage />} />
      <Route path="/recommended-books" element={<RecommendedBooksPage />} />
    </Routes>
  </Router>
  )
};

export default App;
