// src/components/ProgressTracker.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

const ProgressTracker = () => {
  const [topics, setTopics] = useState([]);
  const [newTopic, setNewTopic] = useState('');
  const [completedTopics, setCompletedTopics] = useState([]);

  useEffect(() => {
    fetchTopics();
  }, []);

  const fetchTopics = async () => {
    try {
      const response = await axios.get('http://localhost:5000/topics');
      setTopics(response.data);
    } catch (error) {
      console.error('Error fetching topics:', error);
    }
  };

  const addTopic = async () => {
    if (newTopic.trim() !== '') {
      try {
        const response = await axios.post('http://localhost:5000/topics', { title: newTopic });
        setTopics([...topics, response.data]);
        setNewTopic('');
      } catch (error) {
        console.error('Error adding topic:', error);
      }
    }
  };

  const toggleCompletion = async (id) => {
    try {
      const topicToToggle = topics.find(topic => topic._id === id);
      const updatedTopic = { ...topicToToggle, completed: !topicToToggle.completed };
      await axios.put(`http://localhost:5000/topics/${id}`, updatedTopic);
      const updatedTopics = topics.map(topic =>
        topic._id === id ? { ...topic, completed: updatedTopic.completed } : topic
      );
      setTopics(updatedTopics);
    } catch (error) {
      console.error('Error toggling completion:', error);
    }
  };

  const completedCount = topics.filter(topic => topic.completed).length;

  const chartData = {
    labels: ['Completed', 'Pending'],
    datasets: [
      {
        label: 'Topics',
        data: [completedCount, topics.length - completedCount],
        backgroundColor: ['#4CAF50', '#FFC107'],
      },
    ],
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen p-6">
      <h1 className="text-3xl font-bold mb-4">Progress Tracker</h1>

      <div className="mb-4">
        <input
          type="text"
          value={newTopic}
          onChange={(e) => setNewTopic(e.target.value)}
          placeholder="Enter a new topic"
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
        />
        <button onClick={addTopic} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded-md shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50">
          Add Topic
        </button>
      </div>

      <div className="mb-4">
        {topics.map(topic => (
          <div key={topic._id} className="flex items-center mb-2">
            <input
              type="checkbox"
              checked={topic.completed}
              onChange={() => toggleCompletion(topic._id)}
              className="mr-2"
            />
            <span className={topic.completed ? 'line-through' : ''}>{topic.title}</span>
          </div>
        ))}
      </div>

      <div className="mb-4">
        <h2 className="text-xl font-bold mb-2">Progress Chart</h2>
        <Bar data={chartData} />
      </div>
    </div>
  );
};

export default ProgressTracker;
