import { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal"; // Install react-modal for the modal functionality

const ResumeTemplates = () => {
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState(null);

  useEffect(() => {
    fetchTemplates();
  }, []);

  const fetchTemplates = async () => {
    try {
      const response = await axios.get("/api/templates");
      setTemplates(response.data);
    } catch (error) {
      console.error("Error fetching templates", error);
    }
  };

  const openPreview = (template) => {
    setSelectedTemplate(template);
  };

  const closePreview = () => {
    setSelectedTemplate(null);
  };

  return (
    <main>
      <Section title="Resume Templates" description="Download resume templates in Word format">
        <ul className="space-y-2">
          {templates.map((template) => (
            <li key={template._id} className="flex items-center justify-between">
              <span className="text-black">{template.name}</span>
              <button
                onClick={() => openPreview(template)}
                className="bg-gradient-to-r from-purple-500/20 to-indigo-500/50 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
              >
                Preview
              </button>
              <a
                href={`/api/download-template/${template._id}`}
                className="bg-gradient-to-r from-purple-500/20 to-indigo-500/50 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
              >
                Download
              </a>
            </li>
          ))}
        </ul>
      </Section>

      {/* Modal for previewing template */}
      <Modal
        isOpen={!!selectedTemplate}
        onRequestClose={closePreview}
        contentLabel="Template Preview"
      >
        {selectedTemplate && (
          <>
            <h2>{selectedTemplate.name}</h2>
            <iframe
              src={`/api/preview-template/${selectedTemplate._id}`}
              title="Template Preview"
              width="100%"
              height="500"
            ></iframe>
            <button onClick={closePreview}>Close</button>
          </>
        )}
      </Modal>
    </main>
  );
};

export default ResumeTemplates;
