import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const [codingResources, setCodingResources] = useState([]);
  const [behavioralQuestions, setBehavioralQuestions] = useState([]);
  const [companyResearch, setCompanyResearch] = useState([]);

  const updateResources = (endpoint, resource) => {
    if (endpoint === 'coding') {
      setCodingResources([...codingResources, resource]);
    } else if (endpoint === 'behavioral-questions') {
      setBehavioralQuestions([...behavioralQuestions, resource]);
    } else if (endpoint === 'company-research') {
      setCompanyResearch([...companyResearch, resource]);
    }
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col">
      <header className="bg-gradient-to-r from-purple-500 to-indigo-500 py-4 px-6 flex justify-between items-center shadow-lg">
        <h1 className="text-3xl font-bold tracking-wider">Admin Dashboard</h1>
        <Link to="/" className="bg-primary-foreground text-primary hover:bg-primary/80 hover:text-primary-foreground px-4 py-2 rounded-full shadow-md transition duration-300">Go to Dashboard</Link>
      </header>

      <main className="flex-1 p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <AdminForm title="Post Coding Practice" description="Add coding practice resources" endpoint="coding" updateResources={updateResources} />
        <AdminForm title="Post Behavioral Questions" description="Add behavioral questions resources" endpoint="behavioral-questions" updateResources={updateResources} />
        <AdminForm title="Post Company Research" description="Add company research resources" endpoint="company-research" updateResources={updateResources} />
      </main>

      <section className="p-6">
        <h2 className="text-2xl font-bold mb-4">Coding Practice</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {codingResources.map((resource, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-black text-2xl font-bold mb-2">{resource.resourceTitle}</h2>
              <p className="text-green-600 mb-2"><a href={resource.resourceUrl} target="_blank" rel="noopener noreferrer">{resource.resourceUrl}</a></p>
              <p className="text-black">{resource.resourceContent}</p>
            </div>
          ))}
        </div>
      </section>

      <section className="p-6">
        <h2 className="text-2xl font-bold mb-4">Behavioral Questions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {behavioralQuestions.map((resource, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-black text-2xl font-bold mb-2">{resource.resourceTitle}</h2>
              <p className="text-green-600 mb-2"><a href={resource.resourceUrl} target="_blank" rel="noopener noreferrer">{resource.resourceUrl}</a></p>
              <p className="text-black">{resource.resourceContent}</p>
            </div>
          ))}
        </div>
      </section>

      <section className="p-6">
        <h2 className="text-2xl font-bold mb-4">Company Research</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {companyResearch.map((resource, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-black text-2xl font-bold mb-2">{resource.resourceTitle}</h2>
              <p className="text-green-600 mb-2"><a href={resource.resourceUrl} target="_blank" rel="noopener noreferrer">{resource.resourceUrl}</a></p>
              <p className="text-black">{resource.resourceContent}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

const AdminForm = ({ title, description, endpoint, updateResources }) => {
  const [formData, setFormData] = useState({
    resourceTitle: '',
    resourceUrl: '',
    resourceContent: ''
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/adminres/${endpoint}`, formData);
      updateResources(endpoint, response.data);
      alert('Resource posted successfully');
    } catch (error) {
      console.error('Error posting resource:', error);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-black text-3xl font-bold mb-2">{title}</h2>
      <p className="text-green-600 mb-4">{description}</p>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="resourceTitle" className="block text-sm text-black font-medium text-gray-700">Title</label>
          <input
            type="text"
            id="resourceTitle"
            name="resourceTitle"
            value={formData.resourceTitle}
            onChange={handleChange}
            required
            className="w-full text-black px-3 py-2 placeholder-gray-400 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="resourceUrl" className="block text-sm text-black font-medium text-gray-700">URL</label>
          <input
            type="url"
            id="resourceUrl"
            name="resourceUrl"
            value={formData.resourceUrl}
            onChange={handleChange}
            required
            className="w-full text-black px-3 py-2 placeholder-gray-400 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="resourceContent" className="block text-sm text-black font-medium text-gray-700">Content</label>
          <textarea
            id="resourceContent"
            name="resourceContent"
            value={formData.resourceContent}
            onChange={handleChange}
            required
            className="w-full text-black px-3 py-2 placeholder-gray-400 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
          />
        </div>
        <button type="submit" className="bg-gradient-to-r from-green-400 to-blue-500 text-white px-4 py-2 rounded-full shadow-md transition duration-300">Post</button>
      </form>
    </div>
  );
};

export default AdminDashboard;
