import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ProgressTracker = () => {
  const [topics, setTopics] = useState([]);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [],
  });

  useEffect(() => {
    fetchTopics();
  }, []);

  const fetchTopics = async () => {
    try {
      const response = await axios.get('http://localhost:5000/topics');
      const data = response.data;
      setTopics(data);
      prepareChartData(data);
    } catch (error) {
      console.error('Error fetching topics:', error.message);
    }
  };

  const prepareChartData = (data) => {
    const completed = data.filter(topic => topic.status === 'completed').length;
    const pending = data.filter(topic => topic.status === 'pending').length;

    setChartData({
      labels: ['Completed', 'Pending'],
      datasets: [
        {
          label: 'Topics',
          data: [completed, pending],
          backgroundColor: ['#4caf50', '#f44336'],
        },
      ],
    });
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen p-6">
      <h1 className="text-3xl font-bold mb-4">Progress Tracker</h1>
      <div className="mb-6">
        <Bar data={chartData} options={{ responsive: true }} />
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {topics.length > 0 ? (
          topics.map(topic => (
            <div key={topic._id} className="bg-white p-6 rounded-lg shadow-md">
              <h3 className="text-2xl font-bold mb-2">{topic.name}</h3>
              <p className="text-gray-700 mb-3">{topic.description}</p>
              <p className="mb-2">
                Status: <span className={`font-bold ${topic.status === 'completed' ? 'text-green-600' : 'text-red-600'}`}>{topic.status}</span>
              </p>
            </div>
          ))
        ) : (
          <p>No topics to display.</p>
        )}
      </div>
    </div>
  );
};

export default ProgressTracker;
