// src/components/ProgressTracker.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

const ProgressTracker = () => {
  const [topics, setTopics] = useState([]);
  const [completed, setCompleted] = useState([]);

  useEffect(() => {
    fetchTopics();
  }, []);

  const fetchTopics = async () => {
    try {
      const response = await axios.get('http://localhost:5000/topics');
      setTopics(response.data);
    } catch (error) {
      console.error('Error fetching topics:', error);
    }
  };

  const handleCompletedChange = (topicId) => {
    const updatedCompleted = completed.includes(topicId)
      ? completed.filter(id => id !== topicId)
      : [...completed, topicId];
    setCompleted(updatedCompleted);
  };

  const chartData = {
    labels: topics.map(topic => topic.title),
    datasets: [
      {
        label: 'Completed',
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(75,192,192,0.4)',
        hoverBorderColor: 'rgba(75,192,192,1)',
        data: topics.map(topic => completed.includes(topic._id) ? 1 : 0)
      }
    ]
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen p-6">
      <h1 className="text-3xl font-bold mb-4">Progress Tracker</h1>
      <div className="mb-4">
        <Bar data={chartData} />
      </div>
      <ul>
        {topics.map(topic => (
          <li key={topic._id}>
            <input
              type="checkbox"
              checked={completed.includes(topic._id)}
              onChange={() => handleCompletedChange(topic._id)}
            />
            <label>{topic.title}</label>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProgressTracker;
