import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Profile() {
  const [user, setUser] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('Token:', token); // Debugging line
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        console.log('User data:', res.data); // Debugging line
        setUser(res.data);
      } catch (err) {
        console.error('Error fetching user data:', err); // Debugging line
        setError('Failed to fetch user data');
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500/20 to-indigo-500/50 ">
      <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md">
        <h1 className="text-3xl font-bold text-center text-gray-900 mb-6">Profile</h1>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        <div className="flex justify-center mb-4">
          <div className="w-24 h-24 rounded-full bg-gray-200 flex items-center justify-center">
            <span className="text-gray-500">No Image</span>
          </div>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium">Name</label>
          <p className="mt-1 p-3 border border-gray-300 rounded w-full">{user.name || 'N/A'}</p>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium">Email</label>
          <p className="mt-1 p-3 border border-gray-300 rounded w-full">{user.email || 'N/A'}</p>
        </div>
      </div>
    </div>
  );
}

export default Profile;
