import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MotivationPage = () => {
  const [mistake, setMistake] = useState('');
  const [user, setUser] = useState('');
  const [quote, setQuote] = useState('');
  const [mistakes, setMistakes] = useState([]);

  useEffect(() => {
    if (user) {
      fetchMistakes();
    }
  }, [user]);

  const fetchMistakes = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/mistakes/${user}`);
      setMistakes(response.data);
    } catch (error) {
      console.error('Error fetching mistakes:', error);
    }
  };

  const handleAddMistake = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/mistakes', { mistake, user });
      fetchMistakes();
      const quoteResponse = await axios.get('http://localhost:5000/api/quote');
      setQuote(quoteResponse.data.quote);
      setMistake('');
    } catch (error) {
      console.error('Error adding mistake:', error);
    }
  };

  const handleDeleteMistake = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/mistakes/${id}`);
      fetchMistakes();
    } catch (error) {
      console.error('Error deleting mistake:', error);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-black text-3xl font-bold mb-2">Motivational Quotes</h2>
      <p className="text-green-600 mb-4">Add your mistakes and get a motivational quote</p>
      <input
        type="text"
        placeholder="Enter your name"
        value={user}
        onChange={(e) => setUser(e.target.value)}
        className="w-full p-2 mb-4 border border-gray-300 rounded"
      />
      <textarea
        placeholder="Describe your mistake here..."
        value={mistake}
        onChange={(e) => setMistake(e.target.value)}
        className="w-full p-2 mb-4 border border-gray-300 rounded"
      />
      <button
        onClick={handleAddMistake}
        className="bg-gradient-to-r from-purple-500/20 to-indigo-500/50 text-white px-4 py-2 rounded-full shadow-md transition duration-300"
      >
        Add Mistake
      </button>
      {quote && (
        <div className="mt-4 p-4 bg-gray-100 border border-gray-300 rounded">
          <p className="text-xl font-semibold">Motivational Quote:</p>
          <p>{quote}</p>
        </div>
      )}
      <ul className="mt-4">
        {mistakes.map((m) => (
          <li key={m.id} className="p-2 mb-2 bg-gray-100 border border-gray-300 rounded flex justify-between items-center">
            <span>{m.mistake}</span>
            <button
              onClick={() => handleDeleteMistake(m.id)}
              className="bg-red-500 text-white px-2 py-1 rounded-full"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MotivationPage;
